package com.muzima.view.forms;import android.app.Activity;import android.content.Context;import android.content.Intent;import android.webkit.JavascriptInterface;import com.muzima.utils.imaging.ImagingIntent;import static com.muzima.utils.imaging.ImagingIntent.KEY_IMAGE_PATH;public class ImagingComponent {    private final static String TAG = "ImagingComponent";    public static final int REQUEST_CODE = 0x0000c0de;    public static final String FORM_UUID = "formUuid";    private final Activity activity;    private String fieldName;    public ImagingComponent(Activity activity) {        this.activity = activity;    }    @JavascriptInterface    public void startImageIntent(String fieldName, String fieldValue, String formUuid) {        this.fieldName = fieldName;        Intent imagingIntent = new Intent(activity.getApplication(), ImagingIntent.class);        imagingIntent.putExtra(FORM_UUID, formUuid);        imagingIntent.putExtra(KEY_IMAGE_PATH, fieldValue);        activity.startActivityForResult(imagingIntent, REQUEST_CODE);    }    public String getFieldName() {        return fieldName;    }    public static String parseActivityResult(int requestCode, int resultCode, Intent intent) {        if (requestCode == REQUEST_CODE) {            if (resultCode == Activity.RESULT_OK) {                String imageUri = intent.getStringExtra("IMAGE_URI");                return imageUri;            }        }        return null;    }}